const image = new Image();
image.src = "data:image/png;base64,";
window.addEventListener("load", () => {
  const canvas = document.getElementById("canvas");
  const context = canvas.getContext("2d");
  canvas.width = 500;
  canvas.height = 506;
  context.drawImage(image, 0, 0, canvas.width, canvas.height);
  const pixcels = context.getImageData(0, 0, canvas.width, canvas.height);
  context.clearRect(0, 0, canvas.width, canvas.height);
  let pareticlesArray = [];
  const numberOfParticle = 5000;

  let mappedImage = [];
  for(let y = 0; y < canvas.height; y++) {
    let row = [];
    for(let x = 0; x < canvas.width; x++) {
      const red = pixcels.data[(y * 4 * pixcels.width) + (x * 4)];
      const green = pixcels.data[(y * 4 * pixcels.width) + (x * 4 + 1)];
      const blue = pixcels.data[(y * 4 * pixcels.width) + (x * 4 + 2)];
      const brightness = calculateRelativeBrightness(red, green, blue);
      const cell = [brightness];

      row.push(cell);
    }
    mappedImage.push(row);
  }

  function calculateRelativeBrightness(red, green, blue) {
    return Math.sqrt(
      (red * red) * 0.299 +
      (green * green) * 0.587 +
      (blue * blue) * 0.114
    ) / 100;
  }

  class Particle {
    constructor() {
      this.x = Math.random() * canvas.width;
      this.y = 0;
      this.speed = 0;
      this.velocity = Math.random() * 0.5;
      this.size = Math.random(); //px
      this.position1 = Math.floor(this.y);
      this.position2 = Math.floor(this.x);
    }

    update() {
      this.position1 = Math.floor(this.y);
      this.position2 = Math.floor(this.x);
      this.speed = mappedImage[this.position1][this.position2][0];

      let movement = (2.5 - this.speed) + this.velocity;
      this.y += movement;

      if (this.y >= canvas.height) {
        this.y = 0;
        this.x = Math.random() * canvas.width;
      }
    }

    draw() {
      context.beginPath();
      context.fillStyle = "rgb(255,255,255)";
      // context.arc(this.x, this.y, this.size, 0, Math.PI * 2); // create circular object
      // context.fill();
      context.fillRect(this.x, this.y, this.size, this.size); // create rectangle object
    }
  }

  function init() {
    for (let i = 0; i < numberOfParticle; i++) {
      pareticlesArray.push(new Particle());
    }
  }

  init();

  function animate() {
    // context.drawImage(image, 0, 0, canvas.width, canvas.height);
    context.globalAlpha = 0.05;
    context.fillStyle = "rgb(0, 0, 0)";
    context.fillRect(0, 0, canvas.width, canvas.height);
    for (let i = 0; i < pareticlesArray.length; i++) {
      pareticlesArray[i].update();
      context.globalAlpha = pareticlesArray[i].speed * 0.5;
      pareticlesArray[i].draw();
    }
    requestAnimationFrame(animate);
  }
  animate();
});